/**
 * APISENSE request helper
 */
var request = require('request');

var query = function(filename, params, callback) {
	request.post('http://xperium.cloudapp.net/Xperium/query/mprocess/file',
		    { 
				form: {
					filename: filename, 
					params: params
				} 
		    },
		    callback
		);
};

module.exports.query = query;